const jwt = global.get("jwt");
const bcrypt = global.get("bcrypt");

if (!msg.payload || !msg.payload.name || !msg.payload.password) {
    msg.payload = { error: "Nome e senha são obrigatórios" };
    msg.statusCode = 400;
    return msg;
}

const { name, password } = msg.payload;

const users = flow.get("users") || [];

const user = users.find(u => u.name === name);
if (!user) {
    msg.payload = { error: "Usuário não encontrado" };
    msg.statusCode = 401;
    return msg;
}

const isMatch = bcrypt.compareSync(password, user.password);
if (!isMatch) {
    msg.payload = { error: "Senha incorreta" };
    msg.statusCode = 401;
    return msg;
}

const token = jwt.sign(
    { uuid: user.uuid, name: user.name, isRoot: user.isRoot }, 
    "secreta", 
    { expiresIn: "1h" }
);

msg.payload = { token };
msg.statusCode = 200;
return msg;

const jwt = global.get("jwt");
const authHeader = msg.headers && msg.headers.Authorization;

if (!authHeader) {
    msg.payload = { error: "Cabeçalho Authorization é obrigatório" };
    msg.statusCode = 401;
    return msg;
}

const token = authHeader.split(' ')[1];
if (!token) {
    msg.payload = { error: "Token JWT não encontrado no cabeçalho Authorization" };
    msg.statusCode = 401;
    return msg;
}

try {
    const decoded = jwt.verify(token, "secreta");
    msg.user = decoded;
    msg.log = `[${new Date().toISOString()}] [${msg.user.username}] [${msg.req.route}] : {"token":"${token}"}`;
} catch (err) {
    msg.payload = { error: "Token JWT inválido ou expirado" };
    msg.statusCode = 401;
    return msg;
}

return msg;

const bcrypt = global.get("bcrypt");
const crypto = global.get("crypto");

if (!msg.payload || !msg.payload.name || !msg.payload.password) {
    msg.payload = { error: "Nome e senha são obrigatórios" };
    msg.statusCode = 400;
    return msg;
}

const { name, password, isRoot, isActived } = msg.payload;

let users = flow.get("users") || [];

const userExists = users.find(u => u.name === name);
if (userExists) {
    msg.payload = { error: "Usuário já existe" };
    msg.statusCode = 400;
    return msg;
}

const newUser = {
    uuid: crypto.randomUUID(),
    name,
    password: bcrypt.hashSync(password, 10),
    isRoot: isRoot || false,
    isActived: isActived !== undefined ? isActived : true
};

users.push(newUser);
flow.set("users", users); 

msg.payload = { message: "Usuário criado com sucesso", user: newUser };
msg.statusCode = 201;
return msg;

const crypto = global.get("crypto");

if (!msg.payload || !msg.payload.creationUserName || !msg.payload.model || !msg.payload.color || !msg.payload.year || !msg.payload.imagePath) {
    msg.payload = { error: "Todos os campos são obrigatórios (creationUserName, model, color, year, imagePath)" };
    msg.statusCode = 400;
    return msg;
}

const { creationUserName, model, color, year, imagePath } = msg.payload;

let vehicles = flow.get("vehicles") || [];

const newVehicle = {
    uuid: crypto.randomUUID(), 
    creationUserName,
    updatedUserName: creationUserName,
    creationDate: new Date().toISOString(),
    updatedDate: new Date().toISOString(),
    model,
    color,
    year,
    imagePath
};

vehicles.push(newVehicle);
flow.set("vehicles", vehicles); 

let logs = flow.get("logs") || [];
logs.push(`[${new Date().toISOString()}][${creationUserName}][POST /vehicles]: ${JSON.stringify(newVehicle)}`);
flow.set("logs", logs);

msg.payload = { message: "Veículo cadastrado com sucesso", vehicle: newVehicle };
msg.statusCode = 201;
return msg;

let vehicles = flow.get("vehicles") || [];

let { page, limit, search, sortBy, order } = msg.req.query;

if (search) {
    const searchLower = search.toLowerCase();
    vehicles = vehicles.filter(v =>
        v.model.modeName.toLowerCase().includes(searchLower) ||
        v.color.colorName.toLowerCase().includes(searchLower) ||
        v.creationUserName.toLowerCase().includes(searchLower)
    );
}

if (sortBy) {
    const orderFactor = order === "desc" ? -1 : 1;
    vehicles.sort((a, b) => (a[sortBy] > b[sortBy] ? orderFactor : -orderFactor));
}

page = parseInt(page) || 1;
limit = parseInt(limit) || 10;
const startIndex = (page - 1) * limit;
const endIndex = startIndex + limit;
const paginatedVehicles = vehicles.slice(startIndex, endIndex);

msg.payload = {
    total: vehicles.length,
    page,
    limit,
    vehicles: paginatedVehicles
};
msg.statusCode = 200;
return msg;

const vehicleId = msg.req.params.id;

let vehicles = flow.get("vehicles") || [];

const vehicleIndex = vehicles.findIndex(v => v.uuid === vehicleId);
if (vehicleIndex === -1) {
    msg.payload = { error: "Veículo não encontrado" };
    msg.statusCode = 404;
    return msg;
}

const requestingUser = msg.req.headers["x-user"] || "desconhecido";

const deletedVehicle = vehicles.splice(vehicleIndex, 1)[0];
flow.set("vehicles", vehicles);

let logs = flow.get("logs") || [];
logs.push(`[${new Date().toISOString()}][${requestingUser}][DELETE /vehicles]: ${JSON.stringify(deletedVehicle)}`);
flow.set("logs", logs);

msg.payload = { message: "Veículo excluído com sucesso", vehicle: deletedVehicle };
msg.statusCode = 200;
return msg;

if (!msg.payload || !msg.payload.filename || !msg.payload.data) {
    msg.payload = { error: "Imagem não recebida ou inválida" };
    msg.statusCode = 400;
    return msg;
}

const { filename, data } = msg.payload;

const uploadDir = '/path/to/upload/directory'; 

const fs = require('fs');
const path = require('path');
const filePath = path.join(uploadDir, filename);

if (!fs.existsSync(uploadDir)) {
    fs.mkdirSync(uploadDir, { recursive: true });
}

fs.writeFile(filePath, data, (err) => {
    if (err) {
        msg.payload = { error: 'Erro ao salvar a imagem' };
        msg.statusCode = 500;
    } else {
        msg.payload = { 
            message: 'Imagem salva com sucesso',
            filePath: filePath 
        };
        msg.statusCode = 200;
    }
});

return msg;

const { user, req } = msg;
const actionPayload = JSON.stringify(req.payload);
msg.log = `[${new Date().toISOString()}] [${user.username}] [${req.route}] : ${actionPayload}`;
node.warn(msg.log);
return msg;

const vehicleId = msg.params.id;
const updatedData = msg.payload;

const vehiclesDatabase = global.get('vehiclesDatabase') || [];

let vehicle = vehiclesDatabase.find(v => v.id === vehicleId);
if (!vehicle) {
    msg.payload = { error: `Veículo com ID ${vehicleId} não encontrado.` };
    msg.statusCode = 404;
    return msg;
}

vehicle = { ...vehicle, ...updatedData };

const index = vehiclesDatabase.findIndex(v => v.id === vehicleId);
vehiclesDatabase[index] = vehicle;

global.set('vehiclesDatabase', vehiclesDatabase);

msg.payload = { success: "Veículo atualizado com sucesso", vehicle };
msg.statusCode = 200;

msg.log = `[${new Date().toISOString()}] [${msg.user.username}] [${msg.req.route}] : ${JSON.stringify(updatedData)}`;

return msg;

const colors = [
    { uuid: '1', colorName: 'Preto' },
    { uuid: '2', colorName: 'Branco' },
    { uuid: '3', colorName: 'Vermelho' },
    { uuid: '4', colorName: 'Cinza' },
    { uuid: '5', colorName: 'Azul' }
];

const models = [
    { uuid: '1', modelName: 'T-Cross' },
    { uuid: '2', modelName: 'Nivus' },
    { uuid: '3', modelName: 'Polo' },
    { uuid: '4', modelName: 'Virtus' },
    { uuid: '5', modelName: 'Saveiro' },
    { uuid: '6', modelName: 'Track' }
];

global.set('colors', colors);
global.set('models', models);

msg.payload = { success: true, message: "Cores e Modelos armazenados com sucesso!" };
return msg;
